package simplefor1;

public class SimpleFor2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		System.out.println("print number from 1 to 10");
		for(i=1;i<=10;i++) {
			
			System.out.println(i);
			
		}//end of for
		System.out.println("Reverse number from 1 to 10");
		for(i=10;i>=1;i--) {
			System.out.println(i);
		}

	}//end of main

}
